mapper.select:
select nickname, userid, email, deleted
from users
where userid = #{userid} and userpw = #{userpw}

select nickname, userid, phone, email, birthday, intro, deleted
from users
where userid = #{userid}

select token
from users
where userid = #{userid}

mapper.update:
update users
set token = #{token}
where userid = #{userid}

update users
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}

update users
set nickname= #{nickname} ,phone= #{phone} ,email = #{email} , birthday = #{birthday} , intro = #{intro}
where userid = #{userid}

update users
set deleted=1
where userid = #{userid}
